found_packet_marker = False
for i in range(3, len(input_str)):
    cmp_len = 4 if not found_packet_marker else 14
    selection = set(input_str[max(0, i - cmp_len + 1) : i + 1])
    if len(selection) == cmp_len:
        print(
            "Found start-of-",
            "packet" if cmp_len == 4 else "message",
            " marker at position ",
            i + 1,
            sep="",
        )
        if found_packet_marker:
            break
        found_packet_marker = True
